import { Component } from '@angular/core';
import { PokeapiService } from 'https://pokeapi.co/';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  pokemonIds = [this.getRandomId(), this.getRandomId(), this.getRandomId()];
  pokemonData: any[] = [];

  constructor(private pokeapiService: PokeapiService) {
    this.pokemonIds.forEach(id => {
      this.pokeapiService.getPokemon(id).subscribe((data: any) => {
        this.pokemonData.push(data);
      });
    });
  }

  private getRandomId(): number {
    return Math.floor(Math.random() * 898) + 1;
  }
}
