import { Component } from '@angular/core';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent {

}
<div class="flex justify-center items-center mt-8">
    <div class="relative mx-auto text-white">
      <input class="border-2 border-gray-300 bg-red-500 h-10 px-5 pr-16 rounded-lg text-sm focus:outline-none"
        type="text" name="search" placeholder="Nome">
    </div>
    <p class="mt-5">This search bar component is part of a larger, open-source library of Tailwind CSS components. Learn
		more
		by going to the official <a class="text-blue-600 hover:underline"
			href="#" target="_blank">Flowbite Documentation</a>.
	</p>
    <script src="https://unpkg.com/flowbite@1.4.0/dist/flowbite.js"></script>

