import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit {

  constructor(){};

  title= "Questo è il pokedex di Ettore";

  ngOnInit(): void {
    console.log("ngOnInIt");
  }

}
<header class="bg-red-500 py-2 h-10 text-center">
  <div class="flex items-center justify-center">
    <img src="assets/pokeball.png" alt="Immagine che ruota" class="h-6 w-6  hover:animate-spin mr-2">
    <h1 class="text-white font-bold">{{ title.toUpperCase() }}</h1>
  </div>
</header>
