import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

}
<div class="flex flex-col justify-center items-center mt-1">
    <img src="https://upload.wikimedia.org/wikipedia/it/8/83/Pok%C3%A9dex_3D.png" class="w-1/4">
  </div>
  <h1 class="ext-4xl text-black font-bold flex flex-col justify-center items-center mt-1">
    Questo pokedex contiene pokemon di vario genere, ovviamente non li tiene tutti, starà a te
    sviluppatore aggiornarlo
  </h1>
  <div class="text-center">
    <button class="bg-red-500 border border-black rounded-md text-white py-2 px-4">
        Vai alla ricerca pokemon
    </button>
</div>
